import React, {useState, useEffect } from 'react'
import LoginDataTable from './LoginDataTable'

function SearchLogins({placeholder, data}) {
  
  const [filteredData, setFilteredData] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");


  useEffect((setFilteredData(data)))

  useEffect(() => {
    const newFilter = data.filter((value) => {
      return value.websiteName.toLowerCase().includes(searchTerm.toLowerCase())
    })
    setFilteredData(newFilter)
    }, [searchTerm])

 
  return (
    <div className='search'>
        <div className='search-input'>
            <input type="text" placeholder={placeholder} onChange={(e) => setSearchTerm(e.target.value)}></input>
            <div className='search-icon'></div>
        </div>
        <div className='search-suggestion'>
        <LoginDataTable 
            logins={filteredData} />
        </div>
    </div>
  )
}

export default SearchLogins
