import React from 'react';
import {useState, useEffect} from 'react';
import Axios from 'axios';
import Navigation from '../components/Navigation';
import LoginData from '../components/LoginData'
import Footer from '../components/Footer';

export function ViewAll() {
  
  const [loginList, setLoginList] = useState([])
  const [nameList, setNameList] = useState([])
  const [searchTerm, setSearchTerm] = useState("")
  const [filteredData, setFilteredData] = useState([]);
  const [typoFix, setTypoFix] = useState([])
  const [errorMessage, setErrorMessage] = useState("")

  function getLogins() {
    Axios.get("http://localhost:4500/api/v1/logins").then((response) => {
      setLoginList(response.data.logins)
    }
  )};

  function getSiteNames() {
    Axios.get("http://localhost:4500/api/v1/logins/websiteNames").then((response) => {
      const websiteNames = response.data.map(wName => {
        return wName.toLowerCase()
      })
      setNameList(websiteNames)
    }
    )
  };
  
  
  useEffect(() => {getLogins()},[]);
  useEffect(() => {getSiteNames()},[]);
  useEffect(() => {
    const newFilter = loginList.filter((value) => {
      return (value.websiteName.toLowerCase()).includes(searchTerm.toLowerCase())
    })
    setFilteredData(newFilter)
    }, [searchTerm])


  const checkSearch = () => {
      const searchTerm = searchTerm.toLowerCase()
      if (nameList.includes(searchTerm)) {
          console.log("website found")
      } else {
          Axios.post("http://localhost:4500/search", {
              searchTerm: searchTerm,
              listOfSites: loginList
          }).then((res) => {
              const suggestion_array = res.data
              setTypoFix(suggestion_array)
              let websitelist = document.getElementById("searchFix");
              suggestion_array.forEach((item)=>{
                  let p = document.createElement("p");
                  p.innerText = item;
                  websitelist.appendChild(p);
              }) 
              setErrorMessage(true)
          })
      }
  }


  return (
    <div>
      <Navigation />
      <div className ="main-content">
        <div className="block-container">
          <div className="block-container-inner">
            <div className="form-field-container">
              <h3>View All</h3>
              <div className='search'>
                <div className='search-input'>
                    <input type="text" placeholder="Search logins.. " onChange={(e) => setSearchTerm(e.target.value)}></input>
                    <div className='search-icon'></div>
                </div>
                <div className='search-suggestion'>
                <table id="login-table" className="table table-striped table-hover table">
                    <tbody>
                        {filteredData.map((login, i) => <LoginData login={login} key={i} />)}
                    </tbody>
                </table>
                </div>
            </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ViewAll;
