import React from 'react';
import {useState, useEffect} from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';
import Navigation from '../components/Navigation';
import Footer from '../components/Footer';
import LoginDataTable from '../components/LoginDataTable';
import SearchBar from '../components/SearchBar';

export function ViewAll() {
 
  const [logins, setLogins] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");


  let websiteArray = []
  logins.map((login) => websiteArray.push(login.websiteName))

  const loadLogins = async () => {
    let base_url = "http://localhost:4500/view-all"
    if (searchTerm !== undefined) {
      const search_url = base_url + "/?searchTerm=" + searchTerm
      const response = await Axios.get(search_url);
      const data = await response.data;
      setLogins(data);
    } else {
      const response = await Axios.get(base_url);
      const data = await response.data;
      setLogins(data);
    }    
  }

  useEffect(() => {
      loadLogins();
  }, []);

  return (
    <div>
      <Navigation />
      <div className ="main-content">
        <SearchBar 
          searchTerm={searchTerm}
          stateFunction={() => setSearchTerm(e.target.value)}
          submitFunction = {loadLogins} />
        <div className="block-container">
          <div className="block-container-inner">
            <div className="form-field-container">
              <h3>View All</h3>
              <LoginDataTable logins= {logins}></LoginDataTable>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  )
}

export default ViewAll;
