import React from 'react';
import {useState, useEffect} from 'react';
import Axios from 'axios';
import Navigation from '../components/Navigation';
import SearchLogins from '../components/SearchLogins';
import Footer from '../components/Footer';

export function ViewAll() {
  
  const [loginList, setLoginList] = useState([])
  const [searchString, setSearchString] = useState("")
  const [typoFix, setTypoFix] = useState([])
  const [errorMessage, setErrorMessage] = useState("")

  function getLogins() {
    Axios.get("http://localhost:4500/api/v1/logins").then((response) => {
      setLoginList(response.data.logins)
    }
  )};

  function getSiteNames() {
    Axios.get("http://localhost:4500/api/v1/logins/websiteNames").then((response) => {
      const websiteNames = response.data.map(wName => {
        return wName.toLowerCase()
      })
      setLoginList(websiteNames)
    }
    )
  };
  
  
  useEffect(() => {getLogins()},[]);
  useEffect(() => {getSiteNames()},[]);

  const checkSearch = () => {
    console.log(loginList)
      const searchTerm = searchString.toLowerCase()
      if (loginList.includes(searchTerm)) {
          console.log("website found")
      } else {
          Axios.post("http://localhost:4500/search", {
              searchTerm: searchTerm,
              listOfSites: loginList
          }).then((res) => {
              const suggestion_array = res.data
              setTypoFix(suggestion_array)
              let websitelist = document.getElementById("searchFix");
              suggestion_array.forEach((item)=>{
                  let p = document.createElement("p");
                  p.innerText = item;
                  websitelist.appendChild(p);
              }) 
              setErrorMessage(true)
          })
      }
  }


  return (
    <div>
      <Navigation />
      <div className ="main-content">
        <div className="block-container">
          <div className="block-container-inner">
            <div className="form-field-container">
              <h3>View All</h3>
              <SearchLogins placeholder="Search all logins..." data={loginList}/>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ViewAll;
