import React from 'react';
import {useState, useEffect} from 'react';
import LoginDataService from '../services/login'
import Navigation from '../components/Navigation';
import Footer from '../components/Footer';

export default function ViewAll() {

  const [loginList, setLoginList] = useState([])
  const [nameList, setNameList] = useState([])
  const [searchTerm, setSearchTerm] = useState('')
  const [filteredData, setFilteredData] = useState([])
  const [suggestion, setSuggestion] = useState("")
  const [suggested, setSuggested] = useState(false)

  useEffect(() => {getLogins(); getSiteNames()},[]);
  useEffect(() => {updateFilter()},[searchTerm] )

  function getLogins() {
    LoginDataService.getAll()
    .then((response) => {
      setLoginList(response.data.logins)
      setFilteredData(response.data.logins)
    }
  )};

  function getSiteNames() {
    LoginDataService.getNames()
    .then((response) => {
      const websiteNames = []
      response.data.map(wName => {
          var websiteName = wName.toLowerCase()
          return websiteNames.push(websiteName)
      })
      setNameList(websiteNames)
    })
  };
  
  function updateFilter() {
      const newFilter = loginList.filter((value) => {
        return (value.websiteName.toLowerCase()).includes(searchTerm.toLowerCase())
      })
        setFilteredData(newFilter)
  }
  
  const suggestionSearch = () => {
    setSuggested(!suggested)
    LoginDataService.suggestTerm(searchTerm, nameList)
    .then((response) => {
        var suggestedTerm = String(response.data.data[0])
        setSuggestion(suggestedTerm)
        if (suggestedTerm === "") {
          var new_string = "No suggestions available."
        } else {
          new_string = `Did you mean <b>${suggestedTerm}</b> ?`      
        }
        document.getElementById("suggestion-result").innerHTML = new_string
    
    })}

  const changeSearchTerm = () =>{
    setSearchTerm(suggestion)
    document.getElementById("search-box").value = suggestion
    setSuggestion("")
    setSuggested(false)
  }

  function openSite(site) {
    const full_site = "https://" + site
    window.open(full_site)
  }


  function deleteLogin(loginId){
    LoginDataService.deleteLogin(loginId)
  }

  function navToUpdate(loginId, websiteName, websiteDomain, username, password){
    data = {
      "websiteName"   : websiteName,
      "websiteDomain" : websiteDomain,
      "username"      : username,
      "pasword"       : password
    }



  }


  return (
    <div>
      <Navigation />
      <div className ="main-content">
        <div className="block-container">
          <div className="block-container-inner">
            <div className="form-field-container">
              <h3>View All</h3>
              <div className='search'>
                <div className='search-input'>
                    <input id="search-box" type="text" placeholder="Search logins.. " onChange={(e) => setSearchTerm(e.target.value)}></input>
                    <div className='search-icon'></div>
                </div>
                <div className='search-suggestion'>
                <table id="login-table" className="table table-striped table-hover table">
                    <tbody>
                        {filteredData.map((login, i) => 
                        <tr className="data-row">
                          <td><button type="submit" className="button-link" onClick={() => {openSite(login.websiteDomain)}} target="_blank" rel="noreferrer" >{login.websiteDomain}</button></td>
                          <td>Username: {login.username}<br/>Password: {login.password}</td>
                          <td className='text-end'>
                          <form action="/edit-login" method="POST">
                          <input type="hidden"name="loginId" id="loginId" value={login._id}/>
                          <input type="hidden"name="websiteName" id="websiteName" value={login.websiteName}/>
                          <input type="hidden"name="websiteDomain" id="websiteDomain" value={login.websiteDomain}/>
                          <input type="hidden"name="username" id="username" value={login.username}/>
                          <input type="hidden"name="password" id="password" value={login.password}/>
                          <button type="submit" className = "btn btn-light btn-small"><i className="bi bi-pencil-square"/> Edit</button>
                          </form>
                          <form>
                          <button type="submit" onClick={() => {deleteLogin(login._id)}} className="btn btn-light btn-small"><i className="bi bi-x-square"/> Delete</button>
                            </form>
                          </td>
                        </tr>)}
                    </tbody>
                </table>
                </div>
                {filteredData.length === 0 && (
                  <div id="search-error">
                    <p id= "suggestion-result">No results found. Click the button for a suggested search based on your saved information.</p>
                    {suggested=== false &&(
                      <button onClick={suggestionSearch}>Get Suggestion</button>
                    )}
                    {suggested===true && ( 
                      <button onClick={changeSearchTerm}>Accept Suggestion</button>
                    )}
                  </div>
                )}
            </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};
