import React from 'react';
import {useState, useEffect} from 'react';
import Axios from 'axios';
import Navigation from '../components/Navigation';
import LoginDataTable from '../components/LoginDataTable'
import Footer from '../components/Footer';

export function ViewAll() {
  
  const [loginData, setLoginData] = useState([])
  const [nameList, setNameList] = useState([])
  const [searchString, setSearchString] = useState('')
  const [typoFix, setTypoFix] = useState([])
  const [errorMessage, setErrorMessage] = useState("")
  const [searchTerm, setSearchTerm] = useState("")
  const [filteredData, setFilteredData] = useState([])


  function getLogins() {
    Axios.get("http://localhost:4500/api/v1/logins").then((response) => {
      setLoginData(response.data.logins)
    }
  )};

  function getSiteNames() {
    Axios.get("http://localhost:4500/api/v1/logins/websiteNames").then((response) => {
      const websiteNames = response.loginList.map(wName => {
        return wName.toLowerCase()
      })
      console.log(websiteNames)
      setNameList(websiteNames)
    }
    )};
  
  useEffect(() => {getLogins()},[]);
  useEffect(() => {getSiteNames()},[]);
  useEffect(() => {setFilteredData(loginData)},[]);


  const handleFilter = (e) => {
    e.preventDefault()
    setSearchTerm(e.target.value)
    const newFilter = loginData.filter((value) => {
      return value.websiteName.toLowerCase().includes(searchTerm.toLowerCase())
    })
    setFilteredData(newFilter)
  }

  const checkSearch = () => {
    console.log(loginData)
      const searchTerm = searchString.toLowerCase()
      if (loginData.includes(searchTerm)) {
          console.log("website found")
      } else {
          Axios.post("http://localhost:4500/search", {
              searchTerm: searchTerm,
              listOfSites: loginData
          }).then((res) => {
              const suggestion_array = res.data
              setTypoFix(suggestion_array)
              let websitelist = document.getElementById("searchFix");
              suggestion_array.forEach((item)=>{
                  let p = document.createElement("p");
                  p.innerText = item;
                  websitelist.appendChild(p);
              }) 
              setErrorMessage(true)
          })
      }
  }

  return (
    <div>
      <Navigation />
      <div className ="main-content">
        <div className="block-container">
          <div className="block-container-inner">
            <div className="form-field-container">
              <h3>View All</h3>
              <div className='search'>
                <div className='search-input'>
                  <input 
                  type="text" 
                  placeholder="Search logins..." 
                  value={searchTerm}
                  onChange={handleFilter}></input>
                <div className='search-icon'></div>
              </div>
              <div className='search-suggestion'>
                <LoginDataTable 
                  logins={filteredData} />
              </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ViewAll;
