import React, { useState, useEffect } from 'react'
import LoginData from '../components/LoginData'

function SearchLogins({placeholder, data}) {
  
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredData, setFilteredData] = useState(searchTerm.length === 0 ? [] : {data} );

  useEffect(() => {
    const newFilter = data.filter((value) => {
      return value.websiteName.toLowerCase().includes(searchTerm.toLowerCase())
    })
    setFilteredData(newFilter)
    console.log(searchTerm)
    console.log(newFilter)
    }, [searchTerm])
 
  return (
    <div className='search'>
        <div className='search-input'>
            <input type="text" placeholder={placeholder} onChange={(e) => setSearchTerm(e.target.value)}></input>
            <div className='search-icon'></div>
        </div>
        <div className='search-suggestion'>
        <table id="login-table" className="table table-striped table-hover table">
            <tbody>
                {filteredData.map((login, i) => <LoginData login={login} key={i} />)}
            </tbody>
        </table>
        </div>
    </div>
  )
}

export default SearchLogins
