import React from 'react';
import {useState, useEffect} from 'react';
import Axios from 'axios';
import Navigation from '../components/Navigation';
import LoginData from '../components/LoginData'
import Footer from '../components/Footer';

export default function ViewAll() {

  const [loginList, setLoginList] = useState([])
  const [nameList, setNameList] = useState([])
  const [searchTerm, setSearchTerm] = useState('')
  const [filteredData, setFilteredData] = useState([])
  const [suggestion, setSuggestion] = useState("")
  const [suggested, setSuggested] = useState(false)


  function getLogins() {
    Axios.get("http://localhost:4500/api/v1/logins").then((response) => {
      setLoginList(response.data.logins)
      setFilteredData(response.data.logins)
    }
  )};

  function getSiteNames() {
    Axios.get("http://localhost:4500/api/v1/logins/websiteNames").then((response) => {
      const websiteNames = response.data.map(wName => {
        return wName.toLowerCase()
      })
      setNameList(websiteNames)
    }
    )
  };
  
  function updateFilter() {
      const newFilter = loginList.filter((value) => {
        return (value.websiteName.toLowerCase()).includes(searchTerm.toLowerCase())
      })
        setFilteredData(newFilter)
  }
  
  useEffect(() => {getLogins()},[]);
  useEffect(() => {getSiteNames()},[]);
  useEffect(() => {updateFilter()},[searchTerm] )


  const suggestionSearch = () => {
    setSuggested(!suggested)
    Axios.post("http://localhost:4500/api/v1/logins/search", {
        searchTerm: searchTerm,
        listOfSites: nameList
      }).then((response) => {
        console.log(response.data)
        setSuggestion(String(response.data.data[0]))
    
    })}

  const changeSearchTerm = async () =>{
    setSearchTerm(suggestion)
    .then(setSuggestion("")
    )
  }

  return (
    <div>
      <Navigation />
      <div className ="main-content">
        <div className="block-container">
          <div className="block-container-inner">
            <div className="form-field-container">
              <h3>View All</h3>
              <div className='search'>
                <div className='search-input'>
                    <input type="text" placeholder="Search logins.. " onChange={(e) => setSearchTerm(e.target.value)}></input>
                    <div className='search-icon'></div>
                </div>
                <div className='search-suggestion'>
                <table id="login-table" className="table table-striped table-hover table">
                    <tbody>
                        {filteredData.map((login, i) => <LoginData login={login} key={i} />)}
                    </tbody>
                </table>
                </div>
                {filteredData.length === 0 && (
                  <div id="search-error">
                    {suggestion !== "" &&(
                    <p>No records found. Would you like to try searching for <b>{suggestion}</b> instead?</p>
                    )}
                    {suggestion === "" &&(
                    <p>No records or close matches found.</p>
                    )}
                    {suggested=== false &&(
                      <button onClick={suggestionSearch}>Get Suggestion</button>
                    )}
                    {suggested===true && ( 
                      <button onClick={changeSearchTerm}>Accept Suggestion</button>
                    )}
                  </div>
                )}
            </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};
