import React, {useState} from 'react'
import LoginDataTable from './LoginDataTable'

function SearchLogins({placeholder, data}) {
  
  const [filteredData, setFilteredData] = useState(data);
  const [searchTerm, setSearchTerm] = useState("");
  
  
  const handleFilter = async (e) => {
    setSearchTerm(e.target.value)
    console.log(searchTerm)
    const newFilter = data.filter((value) => {
      return value.websiteName.toLowerCase().includes(searchTerm.toLowerCase())
    })
    setFilteredData(newFilter)
  }
  
  return (
    <div className='search'>
        <div className='search-input'>
            <input type="text" placeholder={placeholder} onChange={handleFilter}></input>
            <div className='search-icon'></div>
        </div>
        <div className='search-suggestion'>
        <LoginDataTable 
            logins={filteredData} />
        </div>
    </div>
  )
}

export default SearchLogins
