import React from 'react';
import {useState, useEffect} from 'react';
import Axios from 'axios';
import Navigation from '../components/Navigation';
import LoginDataTable from '../components/LoginDataTable'
import Footer from '../components/Footer';

export function ViewAll() {
  
  const [loginList, setLoginList] = useState([])

  function getLogins() {
    Axios.get("http://localhost:4500/api/v1/logins").then((response) => {
      setLoginList(response.data.logins)
    }
  )};

  useEffect(() => {getLogins()},[]);
  

  function searchLogins() {
    // Declare variables
    var input, filter, table, tr, td, i, txtValue;
    input = document.getElementById("myInput");
    filter = input.value.toUpperCase();
    table = document.getElementById("myTable");
    tr = table.getElementsByTagName("tr");
  
    // Loop through all table rows, and hide those who don't match the search query
    for (i = 0; i < tr.length; i++) {
      td = tr[i].getElementsByTagName("td")[0];
      if (td) {
        txtValue = td.textContent || td.innerText;
        if (txtValue.toUpperCase().indexOf(filter) > -1) {
          tr[i].style.display = "";
        } else {
          tr[i].style.display = "none";
        }
      }
    }
  }

  return (
    <div>
      <Navigation />
      <div className ="main-content">
        <div className="block-container">
          <div className="block-container-inner">
            <div className="form-field-container">
              <h3>View All</h3>
              <input type="text" id="myInput" onkeyup={searchLogins} placeholder="Search logins.."></input>
              <LoginDataTable 
                logins={loginList} />
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ViewAll;
