import React from 'react';
import {useState, useEffect} from 'react';
import Axios from 'axios';



function Search() {

    const [loginList, setLoginList] = useState([])
    const [searchString, setSearchString] = useState("")
    const [typoFix, setTypoFix] = useState([])
    const [errorMessage, setErrorMessage] = useState("")


    function getLogins() {
      Axios.get("http://localhost:4500/view-all").then((response) => {
        setLoginList(response.data)
      }
    )};

    function createNameArray(logins) {
        const nameArray = []
        logins.map((login) => nameArray.push((login.websiteName).toLowerCase()))
    return(nameArray)
    }

    useEffect(() => {getLogins()},[]);

    const checkSearch = () => {
        const listOfSites = createNameArray(loginList)
        const searchTerm = searchString.toLowerCase()
        if (listOfSites.includes(searchTerm)) {
            console.log("website found")
        } else {
            Axios.post("http://localhost:4500/search", {
                searchTerm: searchTerm,
                listOfSites: listOfSites
            }).then((res) => {
                const suggestion_array = res.data
                setTypoFix(suggestion_array)
                let websitelist = document.getElementById("searchFix");
                suggestion_array.forEach((item)=>{
                    let p = document.createElement("p");
                    p.innerText = item;
                    websitelist.appendChild(p);
                }) 
                setErrorMessage(true)
            })
        }
    }


  return (
        <div>
          <Navigation />
          <div className ="main-content">
            <div className="block-container">
              <div className="block-container-inner">
                <div className="form-field-container">
                  <h3>Search Page</h3>
                  <div className="form-field--entry">
                    <input 
                        type="search" 
                        placeholder="Search..."
                        value={searchString}
                        onChange={(e) => setSearchString(e.target.value)}/>
                  </div>
                  <div className="form-field--entry" style={{display:errorMessage ? 'block': 'none'}} >
                    <p>Search returned no results, did you mean: </p>
                    <div id="searchFix"></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <Footer />
        </div>
      );
  }


export default Search
