import React from 'react';
import {useState, useEffect} from 'react';
import Axios from 'axios';
import * as fs from 'fs';

function Search() {

    const pipeline = '.../suggestion-search-microservice'

    const [loginList, setLoginList] = useState([])
    const [searchString, setSearchString] = useState("")

    function getLogins() {
      Axios.get("http://localhost:4500/view-all").then((response) => {
        setLoginList(response.data)
      }
    )};

    function createNameArray(logins) {
        const nameArray = []
        logins.map((login) => nameArray.push((login.websiteName).toLowerCase()))
    return(nameArray)
    }

    useEffect(() => {getLogins()},[]);

    const checkSearch = () => {
        const listOfSites = createNameArray(loginList)
        if (listOfSites.includes(searchString.toLowerCase())) {
            console.log("website found")
        } else {
            fs.writeFileSync(pipeline, "website not found")
        }
    }



  return (
    <div>
      <h1>Search Page</h1>
      <div className="input-group">
        <div className="form-outline">
            <input 
                type="search" 
                placeholder="Search..."
                value={searchString}
                onChange={(e) => setSearchString(e.target.value)}/>
        </div>
        <button type="button" onClick={checkSearch} >Search</button>
        </div>
    </div>
  )
}

export default Search
