import React from 'react';
import {useState, useEffect} from 'react';
import Axios from 'axios';
import Navigation from '../components/Navigation';
import LoginDataTable from '../components/LoginDataTable'
import Footer from '../components/Footer';

export function ViewAll() {
  
  const [loginList, setLoginList] = useState([])
  const [searchString, setSearchString] = useState("")

  function getLogins() {
    Axios.get("http://localhost:4500/view-all").then((response) => {
      setLoginList(response.data)
    }
  )};

  function getLoginsByFilter(searchTerm) {
    Axios.post("http://localhost:4500/view-filter", searchTerm).then((response) => {
      setLoginList(response.data)
    }, []);
  }

  useEffect(() => {getLogins()},[]);
  
  const handleSearch = (e) => {
    setSearchString(e.target.value);
    getLoginsByFilter({
      searchTerm: searchString
    });
  };

  return (
    <div>
      <Navigation />
      <div className ="main-content">
        <div className="block-container">
          <div className="block-container-inner">
            <div className="d-flex" role="search">
              <input 
              className="form-control me-2" 
              type="search" placeholder="Search..." 
              onChange={handleSearch}/>
            </div> 
            <div className="form-field-container">
              <h3>View All</h3>
              <LoginDataTable 
                logins={loginList} />
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ViewAll;
