import React from 'react';
import {useState, useEffect} from 'react';
import Axios from 'axios';
import Navigation from '../components/Navigation';
import Footer from '../components/Footer';
import LoginData from '../components/LoginData'
import SearchBar from '../components/SearchBar';

export function ViewAll() {
 
  const [logins, setLogins] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  let websiteArray = []
  logins.map((login) => websiteArray.push(login.websiteName))
  console.log()

  const loadLogins = async () => {
    const response = await Axios.get("http://localhost:4500/view-all");
    const data = await response.data;
    setLogins(data);
}

  useEffect(() => {
      loadLogins();
  }, []);

  return (
    <div>
      <Navigation />
      <div className ="main-content">
        <div className="block-container">
          <div className="block-container-inner">
          <div className="d-flex" role="search">
            <input 
            className="form-control me-2" 
            type="search" placeholder="Search..." 
            onChange={e => {setSearchTerm(e.target.value)}}/>
        </div> 
            <div className="form-field-container">
              <h3>View All</h3>
              <table id="login-table" className="table table-striped table-hover">
                <tbody>
                  <tr>
                    <th>Website</th>
                    <th>Login</th>
                    <th className="text-end"></th>
                  </tr>
                      {logins.filter((val) => {
                          if (searchTerm !== "") {
                              return val.websiteName.toLowerCase().inclues(searchTerm.toLowerCase())
                          } else {
                              return val
                          }
                      }).map((login, i) => <LoginData login={login} key={i} />)}
                </tbody>
            </table>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  )
}

export default ViewAll;
